graphql.query:
query ($query:QueryStatement $constraint:QueryStatement)
{
	oAuthPaginateApplications (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:QueryStatement)
{
	oAuthGetApplications (query: $query)
	{
		id
		name
		code
		secret
		isMaster
		createdAt
		updatedAt
	}
}

query ($query:QueryStatement)
{
	oAuthFindApplication (query: $query)
	{
		id
		name
		code
		secret
		isMaster
		createdAt
		updatedAt
	}
}

query ($id:ID)
{
	oAuthFindApplicationById (id:$id)
	{
		id
		name
		code
		secret
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($payload:OAuthUpdateApplicationInput!)
{
	oAuthUpdateApplication (payload:$payload)
	{
		id
		name
		code
		secret
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($id:ID!)
{
	oAuthDeleteApplicationById (id:$id)
	{
		id
		name
		code
		secret
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($query:QueryStatement)
{
	oAuthDeleteApplications (query: $query)
	{
		id
		name
		code
		secret
		isMaster
		createdAt
		updatedAt
	}
}
