graphql.query:
query ($query:QueryStatement $constraint:QueryStatement)
{
	oAuthPaginateRefreshTokens (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:QueryStatement)
{
	oAuthGetRefreshTokens (query: $query)
	{
		id
		accessTokenId
		token
		isRevoked
		expiresAt
		createdAt
		updatedAt
	}
}

query ($query:QueryStatement)
{
	oAuthFindRefreshToken (query: $query)
	{
		id
		accessTokenId
		token
		isRevoked
		expiresAt
		createdAt
		updatedAt
	}
}

query ($id:ID)
{
	oAuthFindRefreshTokenById (id:$id)
	{
		id
		accessTokenId
		token
		isRevoked
		expiresAt
		createdAt
		updatedAt
	}
}

mutation ($payload:OAuthUpdateRefreshTokenInput!)
{
	oAuthUpdateRefreshToken (payload:$payload)
	{
		id
		accessTokenId
		token
		isRevoked
		expiresAt
		createdAt
		updatedAt
	}
}

